import React, { Component, Fragment } from "react";
import EditRow from "./EditRow";
import "./Table.css";

function Table() {
  function populateTable() {
    const formData = JSON.parse(localStorage.getItem("Form-Data")) || [];
    return formData.map((data, i) => {
      return (
        <tr key={i}>
          <td>{data.nome}</td>
          <td>{data.email}</td>
          <td>{data.dataNasc}</td>
          <td>{data.estado}</td>
          <td>{data.cidade}</td>
          <td>{data.sexo}</td>
          <td>{data.ativo}</td>
          <td className="edit-column">
            <input
              className="edit-button"
              type="button"
              id={"edit" + i}
              value="Editar"
              onClick=""
            ></input>
          </td>
          <td className="delete-column">
            <input
              className="delete-button"
              type="button"
              id={"delete" + i}
              value="Deletar"
              onClick={(i) => removeRow(i)}
            />
          </td>
        </tr>
      );
    });
  }

  function dataLoad() {}

  function removeRow(index) {
    const formData = JSON.parse(localStorage.getItem("Form-Data")) || [];

    const confirmation = window.confirm(
      "Você tem certeza que deseja excluir este usuário?"
    );

    if (confirmation) {
      formData.splice(index, 1);
      localStorage.setItem("Form-Data", JSON.stringify(formData));
      window.location.reload();
    } else {
      return false;
    }
  }

  return (
    <div className="div-table">
      <h1>Tabela em React</h1>

      <form>
        <table className="table-container" id="table-exercicio">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Email</th>
              <th>Data de Nasc.</th>
              <th>Estado</th>
              <th>Cidade</th>
              <th>Sexo</th>
              <th>Ativo</th>
              <th className="edit-column">Editar</th>
              <th className="delete-column">Excluir</th>
            </tr>
          </thead>
          <tbody id="table-body">{populateTable()}</tbody>
        </table>
      </form>
    </div>
  );
}

export default Table;
