import React, { useState } from "react";

function App() {
  // Equivalente a todo o construtor padrão do class component
  const [tarefas, setTarefas] = useState([
    // const [*nome do state, *nome do set state] = useState(*Como inicia)
    "Pagar Contas",
    "Estudar React Hooks",
  ]);

  const [input, setInput] = useState("");
  const [testText, setTestText] = useState("Não tire minhas letras!");

  function handleChange() {
    setTestText(testText.slice(0, testText.length - 1));
    // Forma de alterar o state, aqui tirando a ultima letra da string testText
    setTarefas([...tarefas, input]);
    // Forma de alterar um array de state,
    // a parte "...tarefas" representa todos os elementos já presentes no array
  }

  return (
    <div>
      <ul>
        {tarefas.map((tarefa, i) => (
          <li key={i}>{tarefa}</li>
        ))}
      </ul>
      <h1>{testText}</h1>
      {/* Não é necessário o uso do this.state para chamar um state
       */}
      <input
        type="text"
        value={input}
        onChange={(evt) => setInput(evt.target.value)}
      ></input>
      <button type="button" onClick={handleChange}>
        {/* Não é mais necessário usar o this antes de chamar uma função,
            já que funções podem ter outras funções dentro de si 
            */}
        Adicionar tarefa
      </button>
    </div>
  );
}

export default App;
