import React, { useState } from "react";

function App() {
  const [tarefas, setTarefas] = useState([
    // Equivalente a todo o construtor padrão do class component
    "Pagar Contas", // const [*nome do state, *nome do set state] = useState(*Como inicia)
    "Estudar React Hooks",
  ]);

  const [nome, setNome] = useState("Arthur");

  return (
    <div>
      <ul>
        {tarefas.map((tarefa, i) => (
          <li key={i}>{tarefa}</li>
        ))}
      </ul>
      <h1>{nome}</h1>
    </div>
  );
}

export default App;
