import React, { useState, Fragment } from "react";
import "./Table.css";
import DropdownEstados from "./DropdownEstados";
import DropdownCidades from "./DropdownCidades";
import Form from "./Form";

function Table() {
  const formData = JSON.parse(localStorage.getItem("Form-Data")) || [];
  const [dados, setDados] = useState(formData);
  const [estado, setEstado] = useState("");
  const [cidade, setCidade] = useState("");
  const [EstadoValues, setEstadoValues] = useState({});
  const [EditContactId, setEditContactId] = useState(null);

  const handleInputChange = (evt) => {
    setEstado(evt.target.value);
    evt.preventDefault();
    const { value, name } = evt.target;
    setEstadoValues({ ...EstadoValues, [name]: value });
  };

  const collectCidade = (evt) => {
    setCidade(evt.target.value);
  };

  function populateTable(data) {
    return (
      <tr key={data.id}>
        <td>{data.nome}</td>
        <td>{data.email}</td>
        <td>{data.dataNasc}</td>
        <td>{data.estado}</td>
        <td>{data.cidade}</td>
        <td>{data.sexo}</td>
        <td>{data.ativo}</td>
        <td className="edit-column">
          <input
            className="edit-button"
            type="button"
            id={"edit" + data.id}
            value="Editar"
            // onClick=""
          ></input>
        </td>
        <td className="delete-column">
          <input
            className="delete-button"
            type="button"
            id={"delete" + data.id}
            value="Deletar"
            onClick={(i) => removeRow(i)}
          />
        </td>
      </tr>
    );
  }

  function editTable(data) {
    return (
      <tr key={"edit " + i}>
        <td>
          <input
            type="text"
            required="required"
            placeholder="Digite um nome..."
            name="nome"
          ></input>
        </td>
        <td>
          <input
            type="text"
            required="required"
            placeholder="Digite um Email..."
            name="email"
          ></input>
        </td>
        <td>
          <input type="date" required="required" name="data"></input>
        </td>
        <td>
          <DropdownEstados onChange={handleInputChange} />
        </td>
        <td>
          <DropdownCidades
            state={EstadoValues.state}
            onChange={collectCidade}
          />
        </td>
        <td>
          <input
            className="horizontal-radio"
            type="radio"
            id="masc"
            name="sexo"
            value="Masculino"
            // onChange={(evt) => {
            //   Form.setSexo(evt.target.value);
            // }}
          />
          <label>Masc.</label>
          <br />
          <input
            className="horizontal-radio"
            type="radio"
            id="fem"
            name="sexo"
            value="Feminino"
            // onChange={(evt) => {
            //   Form.setSexo(evt.target.value);
            // }}
          />
          <label>Femin.</label>
        </td>
        <td>
          <input
            className="horizontal-checkbox"
            type="checkbox"
            id="active"
            // onChange={Form.changeAtivoState}
          />
        </td>
      </tr>
    );
  }

  function removeRow(index) {
    const formData = JSON.parse(localStorage.getItem("Form-Data")) || [];

    const confirmation = window.confirm(
      "Você tem certeza que deseja excluir este usuário?"
    );

    if (confirmation) {
      formData.splice(index, 1);
      localStorage.setItem("Form-Data", JSON.stringify(formData));
      window.location.reload();
    } else {
      return false;
    }
  }

  return (
    <div className="div-table">
      <h1>Tabela em React</h1>

      <form>
        <table className="table-container" id="table-exercicio">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Email</th>
              <th>Data de Nasc.</th>
              <th>Estado</th>
              <th>Cidade</th>
              <th>Sexo</th>
              <th>Ativo</th>
              <th className="edit-column">Editar</th>
              <th className="delete-column">Excluir</th>
            </tr>
          </thead>
          <tbody id="table-body">
            {dados.map((data, i) => (
              <>
                {EditContactId === data.id
                  ? editTable(data)
                  : populateTable(data, i)}
              </>
            ))}
          </tbody>
        </table>
      </form>
    </div>
  );
}

export default Table;
