import React, { useState } from "react";
import "./Table.css";
import DropdownEstados from "./DropdownEstados";
import DropdownCidades from "./DropdownCidades";
import Form from "./Form";

function Table() {
  const formData = JSON.parse(localStorage.getItem("Form-Data")) || [];
  const [dados, setDados] = useState(formData);

  function populateTable(data, i) {
    return (
      <tr key={i}>
        <td>{data.nome}</td>
        <td>{data.email}</td>
        <td>{data.dataNasc}</td>
        <td>{data.estado}</td>
        <td>{data.cidade}</td>
        <td>{data.sexo}</td>
        <td>{data.ativo}</td>
        <td className="edit-column">
          <input
            className="edit-button"
            type="button"
            id={"edit" + i}
            value="Editar"
            onClick=""
          ></input>
        </td>
        <td className="delete-column">
          <input
            className="delete-button"
            type="button"
            id={"delete" + i}
            value="Deletar"
            onClick={(i) => removeRow(i)}
          />
        </td>
      </tr>
    );
  }

  function editTable() {
    return (
      <td>
        <tr>
          <td>
            <input
              type="text"
              required="required"
              placeholder="Digite um nome..."
              name="nome"
            ></input>
          </td>
          <td>
            <input
              type="text"
              required="required"
              placeholder="Digite um Email..."
              name="email"
            ></input>
          </td>
          <td>
            <input type="date" required="required" name="data"></input>
          </td>
          <td>
            <DropdownEstados />
          </td>
          <td>
            <DropdownCidades />
          </td>
          <td>
            <label className="label-left">Sexo</label>
            <input
              className="horizontal-radio"
              type="radio"
              id="masc"
              name="sexo"
              value="Masculino"
              onChange={(evt) => {
                Form.setSexo(evt.target.value);
              }}
            />
            <label>Masculino</label>
            <input
              className="horizontal-radio"
              type="radio"
              id="fem"
              name="sexo"
              value="Feminino"
              onChange={(evt) => {
                Form.setSexo(evt.target.value);
              }}
            />
            <label>Feminino</label>
          </td>
          <td>
            <label className="label-left">Usuário Ativo?</label>
            <input
              className="horizontal-checkbox"
              type="checkbox"
              id="active"
              onChange={Form.changeAtivoState}
            />
            <label>Ativo</label>
          </td>
        </tr>
      </td>
    );
  }

  function removeRow(index) {
    const formData = JSON.parse(localStorage.getItem("Form-Data")) || [];

    const confirmation = window.confirm(
      "Você tem certeza que deseja excluir este usuário?"
    );

    if (confirmation) {
      formData.splice(index, 1);
      localStorage.setItem("Form-Data", JSON.stringify(formData));
      window.location.reload();
    } else {
      return false;
    }
  }

  return (
    <div className="div-table">
      <h1>Tabela em React</h1>

      <form>
        <table className="table-container" id="table-exercicio">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Email</th>
              <th>Data de Nasc.</th>
              <th>Estado</th>
              <th>Cidade</th>
              <th>Sexo</th>
              <th>Ativo</th>
              <th className="edit-column">Editar</th>
              <th className="delete-column">Excluir</th>
            </tr>
          </thead>
          <tbody id="table-body">
            {dados.map((data, i) => populateTable(data, i))}
          </tbody>
        </table>
      </form>
    </div>
  );
}

export default Table;
